递归方法实现深度克隆原理：遍历对象、数组直到里边都是基本数据类型，然后再去复制，就是深度拷贝。  function getType(value){
 return Object.prototype.toString.call(value).slice(8, -1)
}
function _clone(target){
 let result
 let type = getType(target)
 if(type === 'Object'){
     result = {}
 }else if(type === 'Array'){
     result = []
 }else {
     result = target
     return
 }
 for(let i in target){
     let value = target[i]
     if(getType(value) === 'Object' || getType(value) === 'Array'){
         result[i] = _clone(value)
     }else {
         result[i] = value
     }
 }
 return result
}


function deepCopy(target){
    const type = typeof target
    if(typeof target !== 'object' || target === null) return target
    var ret = target instanceof Array ? [] : {}
    for(let key in target){
        if(target.hasOwnProperty(key)){
            ret[key] = (typeof target[key] !== 'object' || typeof target[key] === null) ? target[key] : deepCopy(target[key])
        }
    }
    return ret
}

