基本实现

三个状态，且不可逆
接收一个函数，参数为resolve, reject
resolve和reject对状态的改变
then catch finally all race resolve reject
const PENDING = 'pending'
const FULFILLED = 'fulfilled'
const REJECTED = 'rejected'
class myPromise{
  constructor(fn){
      this.state = PENGING
      this.value = undefined
      this.reason = undefined
      const resolve = value => {
          if(this.state === PENDING){
              this.state = FULFILLED
              this.value = value
          }
      }
      const reject = reason => {
          if(this.state === PENDING){
              this.state = REJECTED
              this.reason = reason
          }
      }
      try{
          fn(resolve, reject)
      }catch(error){
          reject(reason)
      }
  }
  then(sucess, fail){
      if(this.state === FULFILLED){
          success(this.value)
      }
      if(this.state === REJECTED){
          fail(this.reason)
      }
  }

  catch(fail){
      return this.then(null, fail)
  }


  finally(fn){
      return this.then(vlaue => {
          fn()
          return value
      }, reason => {
          fn()
          throw reason
      })
  }

  resolve(value){
      return new myPromise((resolve, reject) => {
          resolve(value)
      })
  }

  reject(reason){
      return new myPromise((resolve, reject) => {
          reject(reason)
      })
  }
  all(promises){
      return new myPromise((resolve, reject) => {
          if(promises.length === 0){
              resolve([])
          }else {
              let reault = []
              let index = 0
              for(let i = 0; i {
                      result[i] = data
                      if(++index === promises.length){
                          resolve(result)
                      }
                  }, err => {
                      reject(err)
                      return
                  })
              }
          }
      })
  }
  race(promises){
      return new myPromise((resolve, reject) => {
          if(promises.length === 0){
              resolve()
          }else {
              let index = 0
              for(let i = 0;i {
                      resolve(data)
                  }, err => {
                      reject(err)
                      return
                  })
              }
          }
      })
  }
}
let p = new myPromise((resolve, reject) => {
  resolve('222')
})
p.then(res => {
  console.log(res)
})
