1.1.1. 实现flat1.1.1. 实现flat
function trans(str){
    let arr = str.split(',').map(item => +item)
    let lastItem = ''
    let i = 0
    return arr.reduce((cal, cur) => {
        if(cal.length){
            lastItem = cal.pop()
            if(lastItem + 1 === cur){
                if(cal[cal.length - 1] === '~'){
                    cal.push(cur)
                }else{
                    cal.push(lastItem, '~', cur)
                }
            }else{
                cal.push(lastItem, ',', cur)
            }
        }else{
            cal.push(cur)
        }
        return cal
    }, []).join('')
}

