function transform(obj, parentKey="", ret = {}){
    for (let key in obj){
        let keyName = `${parentKey}${key}`
        if(typeof obj[key]!== 'null' && typeof obj[key] === 'object'){
            transform(obj[key], keyName+'.', ret)
        }else{
            ret[keyName] = obj[key]
        }
    }
    return ret
}

